INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157699, 'Tofacitinib', 'Propafenone', 'Moderate', 'Tofacitinib has been shown to decrease heart rate and prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta-blockers, digoxin, alpha-2 adrenoceptor agonists) may result in elevated risk of conduction disturbances and atrioventricular block.', NULL, 'According to the manufacturer, concomitant use of tofacitinib with other agents that lower heart rate and/or prolong the PR interval should be avoided when possible.', NULL, 'According to the manufacturer, concomitant use of tofacitinib with other agents that lower heart rate and/or prolong the PR interval should be avoided when possible. Caution is advised if concomitant use is necessary, particularly in patients with known conduction problems or severe cardiac disease. An ECG should be considered in these patients before initiating concomitant therapy and after titration to steady-state. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeats.', 'Synergism', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, Upadacitinib, Apremilast, Inebilizumab, Cladribine, Belumosudil, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157868/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157700, 'Tolterodine', 'Propafenone', 'Minor', 'Coadministration with drugs that are inhibitors of CYP450 2D6 may increase the plasma concentrations of tolterodine, which is primarily metabolized by this isoenzyme in patients that are extensive metabolizers, or EMs (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent). Moreover, since the active 5-hydroxymethyl metabolite (5-HM) generated by CYP450 2D6 is equipotent compared with tolterodine, and the sum of unbound serum concentrations of tolterodine and 5-HM are only 25% higher during the interaction, little alteration in the overall pharmacologic activity would be expected. Thus, dosage reductions should not be necessary.', NULL, '-', NULL, '-', 'Metabolism', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Avanafil, Phenyl salicylate, Tadalafil, More', 'Tocainide, Procainamide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157869/', '[1] Brynne N, Svanstrom C, AbergWistedt A, Hallen B, Bertilsson L "Fluoxetine inhibits the metabolism of tolterodin-pharmacokinetic implications and proposed clinical relevance." Br J Clin Pharmacol 48 (1999): 553-63[2] "Product Information. Detrol (tolterodine)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157701, 'Toremifene', 'Propafenone', 'Major', 'Toremifene has the potential to prolong the QT interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of toremifene with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of toremifene with other drugs that can prolong the QT interval should generally be avoided. If treatment with other QT-prolonging drugs is required, interruption of toremifene therapy should be considered. Caution and clinical monitoring are recommended if concomitant use is unavoidable. In patients at increased risk, electrocardiograms (ECGs) are recommended at baseline and as clinically indicated. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Anastrozole, Fulvestrant, Letrozole, Aminoglutethimide, Exemestane', 'Lidocaine, Tocainide, Mexiletine, Bretylium, Moricizine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157870/', '[1] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cana