INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157714, 'Venetoclax', 'Propafenone', 'Major', 'Coadministration with P-glycoprotein (P-gp) inhibitors may increase the plasma concentrations of venetoclax, which is a substrate of this efflux transporter.', NULL, 'Concomitant use of venetoclax with P-gp inhibitors should generally be avoided.', NULL, 'Concomitant use of venetoclax with P-gp inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends that venetoclax dosage be reduced by at least 50%. The dosage used prior to initiating the P-gp inhibitor may be resumed 2 to 3 days after discontinuation of the inhibitor.', 'Absorption', 'Lidocaine, Tocainide, Procainamide, Flecainide, Disopyramide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157883/', '[1] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[5] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
