INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157832, 'Epinephrine (topical)', 'Propranolol', 'Moderate', 'Coadministration of non-cardioselective beta-blockers, such as propranolol, with topical solutions containing epinephrine may enhance the hypertensive effect of epinephrine, which may increase the risk of cardiac arrest and hypertensive stroke. The mechanism of this interaction involves beta-blocker antagonism of epinephrine''s beta-agonist effects which results in unopposed alpha-stimulated vasoconstriction and reflexive decreases in heart rate.', NULL, 'Caution and close monitoring of cardiovascular status (i.', NULL, 'Caution and close monitoring of cardiovascular status (i.e., blood pressure, heart rate) are recommended when topical solutions containing epinephrine are administered to patients treated with non-cardioselective beta-blockers. Some authorities recommend avoiding treatment with topical solutions containing epinephrine (AU; Laceraine Topical Wound Anaesthetic(R)) in patients receiving a non-cardioselective beta-blocker.', 'Synergism', 'Felodipine, Amlodipine, Metoprolol, Nebivolol, Nifedipine, Bisoprolol, Ivabradine, Acetylsalicylic acid, Atenolol, Acebutolol, Metoprolol, More', 'Minocycline, Chlorhexidine, Mometasone furoate, Umeclidinium, Aclidinium, Mometasone, Cromoglicic acid, Ipratropium, Beclomethasone dipropionate, Eltrombopag, Romiplostim, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158002/', '[1] Houben H, Thien TH, De Boo TH, et al "Influence of selective and non-selective beta-adrenoceptor blockade on the haemodynamic effect of adrenaline during combined antihypertensive drug therapy." Clin Sci 57 (1979): s397-9[2