INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157854, 'Galantamine', 'Propranolol', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Ginkgo biloba, Aducanumab', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158025/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Cognex (tacrine)." Parke-Davis, Morris Plains, NJ.[3] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCI and digoxin: assessment of pharmacokinetic changes." Br J Clin Pharmacol 46(Suppl 1) (1998): 40-4[4] Tavassoli N, Sommet A, Lapeyre-Mestre M, Bagheri H, Montrastruc JL "Drug interactions with cholinesterase inhibitors : an analysis of the French pharmacovigilance database and a comparison of two national drug formularies (vidal, british national formulary)." Drug Saf 30 (2007): 1063-71[5] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157855, 'Garlic', 'Propranolol', 'Minor', 'Garlic has been found in some studies to lower blood pressure and may theoretically potentiate the effects of hypotensive agents. A meta-analysis of 11 randomized, placebo-controlled studies showed a mean decrease of 4.6 mmHg for systolic blood pressure and no significant effect for diastolic blood pressure in subjects treated with garlic (most often garlic powder 600 to 900 mg/day, providing 3.6 to 5.4 mg of the active component allicin) compared to placebo.', NULL, '-', NULL, '-', 'Synergism', 'Ivabradine', NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158026/', '[1] Tattelman E "Health effects of garlic." Am Fam Physician 72 (2005): 103-6[2] Richard CL, Jurgens TM "Effects of natural health products on blood pressure." Ann Pharmacother 39 (2005): 712-20', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157856, 'Givosiran', 'Propranolol', 'Moderate', 'Coadministration with givosiran may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 1A2 and/or CYP450 2D6. The proposed mechanism is decreased clearance due to givosiran-mediated inhibition of CYP450 1A2 and 2D6 isoenzymes.', NULL, 'Caution and monitoring are recommended when givosiran is given with drugs that are substrates of CYP450 1A2 and/or 2D6.', NULL, 'Caution and monitoring are recommended when givosiran is given with drugs that are substrates of CYP450 1A2 and/or 2D6. Concomitant use should generally be avoided with CYP450 1A2 or 2D6 substrates that have a narrow therapeutic range where minimal concentration changes may lead to serious or life-threatening toxicities. If concomitant use is unavoidable, the dosage of the CYP450 1A2 or 2D6 substrate should be reduced according to approved product labeling or clinical response and tolerance. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever givosiran is added to or withdrawn from therapy with these drugs.', 'Metabolism', 'Felodipine, Amlodipine, Nifedipine, Bisoprolol, Ivabradine, Sotalol, Acetylsalicylic acid, Atenolol, Acebutolol, Labetalol, Nadolol, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, Agalsidase beta, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158027/', '[1] "Product Information. Givlaari (givosiran)." Alnylam Pharmaceuticals, Cambridge, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157857, 'Glucagon', 'Propranolol', 'Minor', 'Beta-blockers may blunt the hyperglycemic reaction to glucagon by interfering with beta-adrenergic-mediated hyperglycemic counter-regulatory mechanisms.', NULL, '-', NULL, '-', 'Antagonism', 'Felodipine, Amlodipine, Nebivolol, Nifedipine, Ivabradine, Acetylsalicylic acid, Nebivolol, Nebivolol', NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158028/', '[1] Mills GA, Horn JR "B-blockers and glucose control." Drug Intell Clin Pharm 19 (1985): 246-51[2] Smith RC, Wilkinson J, Hull RL "Glucagon for propranolol overdose." JAMA 254 (1985): 2412[3] Smith RC, Wilkinson DO, Hull RL "Glucagon for propranolol overdose." JAMA 254 (1985): 2412[4] Messerli FH, Kuchel O, Tolis G, Hamet P, Fraysse J, Genest J "Effects of B-adrenergic blockade on plasma cyclic AMP and blood sugar responses to glucagon and isoproterenol in man." Int J Clin Pharmacol 14 (1976): 189-94[5] Pun KK, Yeung CK, Chak W, et al "Effects of selective and non-selective beta-blockers on the alanine and free fatty acid responses to glucagon challenge in hemodialysis patients." Clin Nephrol 26 (1986): 222-6', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157858, 'Glycerol phenylbutyrate', 'Propranolol', 'Moderate', 'Coadministration of glycerol phenylbutyrate may lead to increased concentrations of CYP450 2D6 substrates. The proposed mechanism is inhibition of CYP450 2D6 by phenylbutyrate, the active moiety of glycerol phenylbutyrate, which has been shown to be an in vitro inhibitor of this isoenzyme.', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol).', NULL, 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate.', 'Metabolism', 'Felodipine, Amlodipine, Nifedipine, Bisoprolol, Ivabradine, Sotalol, Acetylsalicylic acid, Atenolol, Nadolol, Esmolol, Betaxolol, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, Agalsidase beta, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158029/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Ravicti (glycerol phenylbutyrate)." Hyperion Therapeutics Inc, South San Francisco, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157859, 'Glycopyrronium', 'Propranolol', 'Moderate', 'Anticholinergic agents frequently cause drowsiness and other central nervous system-depressant effects that may be additive with those induced by beta blockers. In addition, these agents may increase heart rate and theoretically may counteract the bradycardic effects of beta blockers. Pharmacokinetically, anticholinergic agents may delay the gastrointestinal absorption of beta blockers and other drugs that are administered orally. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents.', NULL, 'Patients should be monitored for potentially excessive CNS adverse effects (e.', NULL, 'Patients should be monitored for potentially excessive CNS adverse effects (e.g., drowsiness, dizziness, lightheadedness, confusion, blurred vision) if anticholinergic agents are used in combination with beta blockers. Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism, Absorption', 'Felodipine, Amlodipine, Nifedipine, Ivabradine, Acetylsalicylic acid, Esmolol', 'Mometasone furoate, Umeclidinium, Aclidinium, Mometasone, Cromoglicic acid, Ipratropium, Beclomethasone dipropionate, Ciclesonide, Umeclidinium, Mometasone furoate, Aclidinium, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158030/', '[1] Regardh CG, Lundborg P, Persson BA "The effect of antacid, metoclopramide, and propantheline on the bioavailability of metoprolol and atenolol." Biopharm Drug Dispos 2 (1981): 79-87[2] Briant RH, Dorrington RE, Ferry DG, Paxton JW "Bioavailability of metoprolol in young adults and the elderly, with additional studies on the effects of metoclopramide and probanthine." Eur J Clin Pharmacol 25 (1983): 353-6[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Clark JM, Seager SJ "Gastric emptying following premedication with glycopyrrolate or atropine." Br J Anaesth 55 (1983): 1195-9', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157860, 'Guanadrel', 'Propranolol', 'Moderate', 'Sympathetic ganglion-blocking or catecholamine-depleting agents such as guanethidine, reserpine, and monoamine oxidase (MAO) inhibitors may potentiate the pharmacologic effects of beta-blockers, which are thought to competitively antagonize catecholamines at cardiac and other peripheral adrenergic neurons. Combining these medications may increase the risk of hypotension, orthostasis, bradycardia, and heart failure due to excessive reduction of sympathetic activity.', NULL, 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents.', NULL, 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents. Patients should contact their doctor if they experience dizziness, lightheadedness, syncope, bradycardia, shortness of breath, difficulty breathing, edema, and/or chest pain.', 'Synergism', 'Felodipine, Acetylsalicylic acid, Amlodipine, Ivabradine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158031/', '[1] "Product Information. Marplan (isocarboxazid)" Roche Laboratories, Nutley, NJ.[2] "Product Information. Matulane (procarbazine)." Roche Laboratories, Nutley, NJ.[3] Kronig MH, Roose SP, Walsh BT, Woodring S, Glassman AH "Blood pressure effects of phenelzine." J Clin Psychopharmacol 3 (1983): 307-10[4] "Product Information. Nardil (phenelzine)." Parke-Dvis, Morris Plains, NJ.[5] Golwyn DH, Sevlie CP "Monoamine oxidase inhibitor hyperten