INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157927, 'Rivastigmine', 'Propranolol', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Ginkgo biloba, Aducanumab', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158098/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Cognex (tacrine)." Parke-Davis, Morris Plains, NJ.[3] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCI and digoxin: assessment of pharmacokinetic changes." Br J Clin Pharmacol 46(Suppl 1) (1998): 40-4[4] Tavassoli N, Sommet A, Lapeyre-Mestre M, Bagheri H, Montrastruc JL "Drug interactions with cholinesterase inhibitors : an analysis of the French pharmacovigilance database and a comparison of two national drug formularies (vidal, british national formulary)." Drug Saf 30 (2007): 1063-71[5] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (157928, 'Rizatriptan', 'Propranolol', 'Moderate', 'Coadministration with propranolol may increase the plasma concentrations of rizatriptan. The exact mechanism is unknown but may involve propranolol inhibition of rizatriptan metabolism via monoamine oxidase, subtype A. No adverse effects were attributed to any of the drug combinations in the studies, and no effect on the active N-monodesmethyl metabolite of rizatriptan was reported.', NULL, 'Caution is advised if rizatriptan must be used with propranolol.', NULL, 'Caution is advised if rizatriptan must be used with propranolol. Patients treated with propranolol should not receive greater than 5 mg of rizatriptan as a single dose, nor more than a maximum of 3 doses in any 24-hour period. It appears that the full range of rizatriptan dose may be used during coadministration with atenolol, metoprolol, nadolol, or timolol.', 'Metabolism', 'Felodipine, Amlodipine, Nebivolol, Nifedipine, Bisoprolol, Ivabradine, Sotalol, Acebutolol, Labetalol, Esmolol, Betaxolol, More', 'Rimegepant, Eptinezumab, Ubrogepant, Galcanezumab, Atogepant, Erenumab, Fremanezumab', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158099/', '[1] "Product Information. Maxalt (rizatriptan)." Merck & Co, Inc, West Point, PA.[2] Goldberg MR, Sciberras D, De Smet M, et al. "Influence of B-adrenoceptor antagonists on the ppharmacokinetics of rizatriptan, a 5-HT IB/ID agonist: differential effects of propranolol, nadolol and metoprolol." Br J Clin Pharmacol 52 (2001): 69-76[3] Olanoff LS, Walle T, Cowart TD, et al "Food effects on propranolol systemic and oral clearance: support for a blood flow hypothe