INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (158075, 'Flecainide', 'Protriptyline', 'Moderate', 'Coadministration with Class IC antiarrhythmic agents may increase the plasma concentrations of some tricyclic antidepressants (TCAs). The proposed mechanism is inhibition of CYP450 2D6, the isoenzyme primarily or partially responsible for the metabolism of most TCAs. Class IC antiarrhythmic agents can cause prolongation of the QT interval. Theoretically, coadministration with other agents such as TCAs that can prolong the QT interval may increase the risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential related to their effects on cardiac conduction.', NULL, 'Caution and clinical monitoring are recommended if a class IC antiarrhythmic agent is prescribed in combination with a tricyclic antidepressant.', NULL, 'Caution and clinical monitoring are recommended if a class IC antiarrhythmic agent is prescribed in combination with a tricyclic antidepressant. Pharmacologic response and serum TCA levels should be monitored more closely whenever a class IC antiarrhythmic agent is added to or withdrawn from therapy, and the TCA dosage adjusted as necessary. Patients should be advised to notify their physician if they experience possible signs and symptoms of TCA toxicity such as excessive sedation, dry mouth, blurred vision, urinary retention, constipation, tachycardia, arrhythmia, and seizures. Patients should seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Metabolism, Synergism', 'Phenelzine, Tranylcypromine, Desvenlafaxine, St. John''s Wort, Nefazodone, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, More', 'Lidocaine, Tocainide, Mexiletine, Bretylium, Moricizine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158246/', '[1] "Product Information. Sinequan (doxepin)." Roerig Division, New York, NY.[2] Katz MR "Raised serum levels of desipramine with the antiarrhythmic propafenone ." J Clin Psychiatry 52 (1991): 432-3[3] Witchel HJ, Hancox JC, Nutt DJ "Psychotropic drugs, cardiac arrhythmia, and sudden death." J Clin Psychopharmacol 23 (2003): 58-77', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (158076, 'Flibanserin', 'Protriptyline', 'Moderate', 'The central nervous system (CNS) effects of flibanserin such as somnolence and fatigue may be potentiated by concomitant use of other agents with CNS depressant effects. The risk of CNS depression is increased if flibanserin is taken during waking hours or with alcohol or other CNS depressants.', NULL, 'Patients receiving flibanserin with other agents that can cause CNS depression should be apprised of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness, and fatigue.', NULL, 'Patients receiving flibanserin with other agents that can cause CNS depression should be apprised of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness, and fatigue. Patients should not drive, operate machinery, or engage in hazardous activities requiring mental alertness and motor coordination for at least 6 hours after taking flibanserin.', 'Synergism', 'Oxitriptan, Tryptophan', 'Bremelanotide, Cabergoline, Black cohosh, Naproxen, Ibuprofen', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158247/', '[1] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.[2] "Product Information. Addyi (flibanserin)." Sprout Pharmaceuticals, Raleigh, NC.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th e