INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (158078, 'Flumazenil', 'Protriptyline', 'Major', 'Severe adverse effects, including fatalities, have been reported following the administration of flumazenil to multiple-drug-overdose patients who have taken large quantities of tricyclic antidepressants. The effects have included seizures and arrhythmias (including ventricular tachycardia). The mechanism of this interaction may be related to a decrease in the protective antiseizure effects of benzodiazepines that patients may have taken concomitantly with overdoses of tricyclic antidepressants.', NULL, 'Administration of flumazenil should be avoided in patients who are known or suspected to have received overdoses of tricyclic antidepressants.', NULL, 'Administration of flumazenil should be avoided in patients who are known or suspected to have received overdoses of tricyclic antidepressants.', 'Synergism', 'Citalopram, Sertraline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Isocarboxazid, Viloxazine, Esketamine, More', 'Hydroxocobalamin, Fomepizole, Ipecac, Nitrous acid, Dexrazoxane, Oxygen, Lanthanum carbonate, Deferoxamine, Levoleucovorin, Arginine, Methionine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158249/', '[1] Marchant B, Wray R, Leach A, Nama M "Flumazenil causing convulsions and ventricular tachycardia." BMJ 299 (1989): 860[2] Bodenham AR "Death after flumazenil." BMJ 299 (1989): 457[3] Mordel A, Winkler E, Almog S, Tirosh M, Ezra D "Seizures after flumazenil administration in a case of combined benzodiazepine and tricyclic antidepressant overdose." Crit Care Med 20 (1992): 1733-4[4] "Flumazenil." Me