INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (158376, 'Fosamprenavir', 'Quazepam', 'Moderate', 'Coadministration with amprenavir or its prodrug, fosamprenavir, may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by amprenavir.', NULL, 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if amprenavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever amprenavir is added to or withdrawn from therapy.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Telaprevir, Velpatasvir, More', 'Temazepam, Remimazolam, Chloral hydrate, Nitrazepam, Thiopental, Paraldehyde, Propiomazine, Meprobamate, Scopolamine, Dexmedetomidine, Methohexital, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158547/', '[1] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.[2] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[3] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.[4] Demarles D, Gillotin C, Bonaventure-Paci S, Vincent I, Fosse S, Taburet AM "Single-dose pharmacokinetics of amprenavir coadministered with grapefruit jui