INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1418566, 'Isradipine', 'Nitroglycerin', 'Moderate', 'Marked, symptomatic orthostatic hypotension has been reported when calcium channel blockers were used in combination with oral controlled-release nitroglycerin.', 'DDInter', 1767317476, 'Dosage adjustments of either class of agents may be necessary. All patients treated with nitroglycerin--with or without calcium channel blockers--should be made aware of possible adverse effects such as dizziness, lightheadedness, and orthostasis, and advised to arise slowly from a sitting or recumbent position.', 'Synergism', 'Dosage adjustments of either class of agents may be necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
