INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1418687, 'Entecavir', 'Dyphylline', 'Moderate', 'Coadministration of entecavir with another drug that is also eliminated by active tubular secretion may result in increased plasma concentrations of one or both drugs due to competitive inhibition of transporters in the renal tubules. Drugs (and/or their metabolites) that are thought to undergo active tubular secretion include acyclovir, allopurinol, aminosalicylic acid, cidofovir, cimetidine, creatine, dyphylline, famciclovir, famotidine, flecainide, ganciclovir, levetiracetam, metformin, methotrexate, midodrine, mycophenolic acid, oseltamivir, pralatrexate, probenecid, procainamide, quinidine, ranitidine, tenofovir, triamterene, trimethoprim, valacyclovir, valganciclovir, zalcitabine, zidovudine, and many of the beta-lactam and quinolone antibiotics.', 'DDInter', 1767317476, 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', 'Excretion', 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1418688, 'Ephedrine', 'Dyphylline', 'Minor', 'Ephedrine-theophylline or ephedrine-dyphylline combinations are used for the treatment of asthma but the efficacy of the combination has been questioned. This combination may lead to increased xanthine side effects. The mechanism is unknown, but may be related to synergistic pharmacologic effects. Patients using this combination should be closely monitored for side effects such as nausea, vomiting, tachycardia, nervousness, or insomnia. If side effects are noted, the dosage of the xanthine may need to be decreased.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
