INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1418813, 'Econazole (topical)', 'Dicoumarol', 'Moderate', 'Limited data suggest that econazole administered topically may potentiate the hypoprothrombinemic effect of warfarin and other coumarins. The mechanism of interaction has not been established, but may involve econazole inhibition of CYP450 isoenzymes responsible for the metabolism of coumarin anticoagulants, in particular CYP450 2C9 and 3A4.', 'DDInter', 1767317476, 'As systemic absorption of econazole is extremely low following topical administration, an interaction with coumarin anticoagulants would not ordinarily be expected. However, close monitoring of International Normalized Ratio (INR) and/or prothrombin time may be indicated, especially when econazole is applied under occlusion or to large areas of the body or the genital area.', 'Metabolism', 'As systemic absorption of econazole is extremely low following topical administration, an interaction with coumarin anticoagulants would not ordinarily be expected.', NULL, NULL, 'Moderate', 'pharmacodynamic');
