INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (158617, 'Indinavir', 'Quetiapine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of quetiapine, which is primarily metabolized by the isoenzyme.', NULL, 'The dosage of quetiapine should be reduced when prescribed with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'The dosage of quetiapine should be reduced when prescribed with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. The product labeling recommends a reduction to one-sixth of the original dosage. Following discontinuation of the CYP450 3A4 inhibitor, the dosage of quetiapine should be increased by 6-fold.', 'Metabolism', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Zanamivir, Velpatasvir, Dolutegravir, Brincidofovir, Remdesivir, More', 'Fluphenazine, Haloperidol, Thiothixene, Amisulpride, Prochlorperazine, Promazine, Thioridazine, Trifluoperazine, Paliperidone, Chlorpromazine, Mesoridazine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158788/', '[1] DeVane CL, Nemeroff CB "Clinical pharmacokinetics of quetiapine - An atypical antipsychotic." Clin Pharmacokinet 40 (2001): 509-22[2] Hantson P, Di Fazio V, Wallemacq P "Toxicokinetic interaction between quetiapine and antiretroviral therapy following quetiapine