INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (158867, 'Rifampicin', 'Quinapril', 'Moderate', 'INTERVAL: Administration of quinapril with some oral medications may significantly decrease their absorption and may result in subtherapeutic serum concentrations. The proposed mechanism is chelation of the drug by the magnesium hydroxide excipient in quinapril tablets.', NULL, 'The manufacturer does not make a specific recommendation, but generally the administration of these drugs and magnesium-containing products should be separated by at least two to three hours.', NULL, 'The manufacturer does not make a specific recommendation, but generally the administration of these drugs and magnesium-containing products should be separated by at least two to three hours.', 'Absorption', 'Moexipril, Perindopril, Benazepril, Trandolapril, Fosinopril, Moexipril, Perindopril, Benazepril, Trandolapril, Fosinopril, Indapamide', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Rifapentine, Streptomycin, Cycloserine, Rifabutin, Isoniazid, Ethambutol, Ethionamide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159039/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Multum Information Services, Inc. Expert Review Panel"[3] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[4] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538[5] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[6] "Product Information. Tequin (gatifloxacin)" Bristol-Myers Squibb, Princeton, NJ.[7] Wallace AW, Victory JM, Amsden GW "Lack of bioequivalence of gatifloxacin when coadministered with calcium-fortified orange juice in healthy volunteers." J Clin Pharmacol 43 (2003): 92-6', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (158868, 'Riociguat', 'Quinapril', 'Moderate', 'Riociguat may potentiate the action of antihypertensive agents and other vasodilators. Symptomatic hypotension may occur. Riociguat stimulates soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cyclic guanosine monophosphate (cGMP). The resultant accumulation of cGMP in vascular smooth muscle cells leads to peripheral vasodilation and blood pressure decreases.', NULL, 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', NULL, 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', 'Synergism', 'Macitentan, Metyrosine', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159040/', '[1] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.[2] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767369485);
