INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (159023, 'Rifampicin', 'Quinine', 'Major', 'Coadministration with rifampin may decrease the plasma concentrations of quinine. The mechanism is rifampin induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of quinine.', NULL, 'Concomitant use of quinine and rifampin should generally be avoided.', NULL, 'Concomitant use of quinine and rifampin should generally be avoided. If clinically appropriate, isoniazid may be a safe and reasonable alternative to rifampin in patients requiring treatment with quinine, as administration of 300 mg/day for 1 week demonstrated no significant effects on the pharmacokinetics of quinine.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159195/', '[1] Pukrittayakamee S, Prakongpan S, Wanwimolruk S, Clemens R, Looareesuwan S, White NJ "Adverse effect of rifampin on quinine efficacy in uncomplicated falciparum malaria." Antimicrob Agents Chemother 47 (2003): 1509-1513[2] "Product Information. Qualaquin (quinine)." AR Scientific Inc, Philadelphia, PA.[3] "Product Information. Rifadin (rifampin)." Hoechst Marion-Roussel Inc, Kansas City, MO.[4] Mirghani RA, Yasar U, Zheng T, et al "Enzyme kinetics for the formation of 3-hydroxyquinine and three new metabolites of quinine in vitro; 3-hydroxylation by CYP3A4 is indeed the major metabolic pathway." Drug Metab Dispos 30 (2002): 1368-71[5] Twum-Barima Y, Carruthers SG "Quinidine-rifampin interaction." N Engl J Med 304 (1981): 1466-9[6] Zhang H, Coville PF, Walker RJ, Miners JO, Birkett DJ, Wanwimolruk S "Evidence for involvement of human CYP3A in the 3-hydroxylation of quinine." Br J C