INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (159069, 'Ticagrelor', 'Quinine', 'Moderate', 'Coadministration with potent P-glycoprotein inhibitors may theoretically increase the plasma concentrations of ticagrelor, which is a substrate of the efflux transporter. However, the interaction has not been studied.', NULL, 'Until more data are available, caution is recommended if ticagrelor must be used with potent P-glycoprotein inhibitors.', NULL, 'Until more data are available, caution is recommended if ticagrelor must be used with potent P-glycoprotein inhibitors. It may be advisable to monitor pharmacologic response to ticagrelor more closely following the addition and withdrawal of these agents. Discontinuation of the concomitant medication may be required if an interaction is suspected.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159241/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[3] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] Ha HR, Chen J, Leuenberger PM, Freiburghaus AU, Follah F "In vitro inhibition of midazolam and quinidine metabolism by flavonoids." Eur J Clin Pharmacol 48 (1995): 367-71', 1767363025);
