INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (159096, 'Aluminum hydroxide', 'Raltegravir', 'Major', 'Coadministration with aluminum-, magnesium-, and/or calcium-containing antacids may reduce the oral bioavailability of raltegravir. The proposed mechanism is chelation of raltegravir by polyvalent cations, but changes in solubility and lipophilicity of raltegravir related to pH increases may also contribute.', NULL, 'Concomitant use of raltegravir with aluminum- and/or magnesium-containing antacids should generally be avoided, even if administration is staggered.', NULL, 'Concomitant use of raltegravir with aluminum- and/or magnesium-containing antacids should generally be avoided, even if administration is staggered. Antacids containing calcium carbonate should not be used with once-daily raltegravir (HD formulation), but may be used without dose adjustment for other raltegravir products.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159268/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Kiser JJ, Bumpass JB, Meditz AL, et al. "Effect of antacids on the pharmacokinetics of raltegravir in human immunodeficiency virus seronegative volunteers." Antimicrob Agents Chemother 54 (2010): 4999-5003[4] Roberts JL, Kiser JJ, Hindman JT, Meditz AL "Virologic failure with a raltegravir-containing antiretroviral regimen and concomitant calcium administration." Pharmacotherapy 31 (2011): 298e-302e[5] Moss DM, Siccardi M, Murphy M, et al. "Divalent metals and pH alter raltegravir disposition in vitro." Antimicrob Agents Chemother 56 (2012): 3020-6[6] "Product Information. Isentress (raltegravir)." Merck & Company Inc, West Point, PA.[7] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767363025);
