INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (159115, 'Rucaparib', 'Raltegravir', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of breast cancer resistance protein (BCRP) (e.g., rosuvastatin) and/or uridine diphosphate glucuronosyltransferase (UGT) 1A1 (e.g., deferasirox).', NULL, 'Caution is advised if rucaparib is used with drugs that are substrates of BCRP and/or UGT1A1, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if rucaparib is used with drugs that are substrates of BCRP and/or UGT1A1, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159287/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (159116, 'Sodium zirconium cyclosilicate', 'Raltegravir', 'Moderate', 'INTERVAL: Because sodium zirconium cyclosilicate can transiently increase gastric pH, it may affect the absorption of coadministered medications that exhibit pH-dependent solubility. Altered efficacy or safety of these medications may occur when they are administered too close to the dosing of sodium zirconium cyclosilicate.', NULL, 'In general, concomitant oral medications should be administered at least 2 hours before or 2 hours after sodium zirconium cyclosilicate.', NULL, 'In general, concomitant oral medications should be administered at least 2 hours before or 2 hours after sodium zirconium cyclosilicate. Separation of dosing times is not needed if it has been determined that the concomitant medication does not exhibit pH-dependent solubility.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159288/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Lokelma (sodium zirconium cyclosilicate)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[3] "Product Information. Lokelma (sodium zirconium cyclosilicate)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (159117, 'Sorafenib', 'Raltegravir', 'Minor', 'Coadministration with inhibitors of uridine diphosphate glucuronosyltransferase (UGT) 1A1 such as atazanavir, nilotinib, or sorafenib may increase the plasma concentrations of raltegravir, which is primarily metabolized by UGT1A1-mediated glucuronidation. However, concurrent use of atazanavir/ritonavir in clinical trials did not result in meaningful differences in the safety of raltegravir. Based on these data, no dosage adjustment of raltegravir is necessary when used together with atazanavir/ritonavir or other UGT1A1 inhibitors.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159289/', '[1] "Product Information. Isentress (raltegravir)." Merck & Company Inc, West Point, PA.[2] "Product Information. Nexavar (sorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767363025);
