INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (159382, 'Sildenafil', 'Ramipril', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', NULL, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents. Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159555/', '[1] "Product Information. Stendra (avanafil)." Vivus Inc, Mountain View, CA.[2] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[3] "Product Information. Levitra (vardenafil)." Bayer, West Haven, CT.[4] Jetter A, Kinzig-Schippers M, Walchner-Bonjean M, et al. "Effects of grapefruit juice on the pharmacokinetics of sildenafil." Clin Pharmacol Ther 71 (2002): 21-29', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (159383, 'Silodosin', 'Ramipril', 'Moderate', 'Additive hypotensive effects may occur when ACE inhibitors are used in combination with alpha-blockers. In the presence of ACE inhibition, the risk and/or severity of first-dose effects associated with alpha-blockers such as postural hypotension and syncope may be increased.', NULL, 'Caution is advised during coadministration of these agents, particularly when initiating an alpha-blocker in the presence of an ACE inhibitor.', NULL, 'Caution is advised during coadministration of these agents, particularly when initiating an alpha-blocker in the presence of an ACE inhibitor. Small initial dosages of the alpha-blocker should be considered and gradually titrated to desired effect, while the ACE inhibitor dosage may also need to be reduced. Hemodynamic responses should be monitored, especially during the first few weeks of therapy. Taking the alpha-blocker at bedtime may minimize the occurrence of orthostatic effects. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159556/', '[1] "Product Information. Xatral (alfuzosin)." Sanofi-Synthelabo Canada Inc, Markham, ON.[2] Baba T, Tomiyama T, Takebe K "Enhancement by an ACE inhibitor of first-dose hypotension caused by an alpha-blocker." N Engl J Med 322 (1990): 1237[3] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[4] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[5] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (159384, 'Sirolimus', 'Ramipril', 'Moderate', 'The use of mTOR (mammalian target of rapamycin) inhibitors has been associated with the development of angioedema, and coadministration with other drugs that are also known to cause angioedema such as ACE inhibitors, angiotensin II receptor antagonists, and renin inhibitors may increase the risk.', NULL, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when mTOR inhibitors are prescribed with other drugs that are also known to cause angioedema.', NULL, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when mTOR inhibitors are prescribed with other drugs that are also known to cause angioedema. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159557/', '[1] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[5] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[6] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538[7] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (159385, 'Sodium ferric gluconate complex', 'Ramipril', 'Moderate', 'Limited data suggest that ACE inhibitors may increase the risk and/or severity of systemic adverse effects associated with parenteral administration of iron. The exact mechanism of interaction is unclear. Certain systemic reactions stemming from intravenous iron therapy are thought to be mediated by inflammatory substances such as bradykinin in response to iron-catalyzed generation of toxic free radicals. Since ACE inhibitors decrease the breakdown of kinins, it is conceivable that they may potentiate these reactions.', NULL, 'No specific intervention is necessary to avert a potential interaction.', NULL, 'No specific intervention is necessary to avert a potential interaction. However, patients should be closely monitored during and after parenteral iron treatment.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159558/', '[1] "Product Information. Ferrlecit (sodium ferric gluconate complex)." sanofi-aventis, Bridgewater, NJ.[2] Michael B, Coyne DW, Folkert VW, Dahl NV, Warnock DG "Sodium ferric gluconate complex in haemodialysis patients: a prospective evaluation of long-term safety." Nephrol Dial Transplant 19 (2004): 1576-80[3] "Product Information. INFeD (iron dextran)." Schein Pharmaceuticals Inc, Florham Park, NJ.[4] Michael B, Coyne DW, Fishbane S, et al. "Sodium ferric gluconate complex in hemodialysis patients: Adverse reactions compared to placebo and iron dextran." Kidney Int 61 (2002): 1830-1839[5] Rolla G, Bucca C, Brussino L "Systemic reactions to intravenous iron therapy in patients receiving angiotensin co