    ) VALUES (1419117, 'Felodipine', 'Erythromycin', 'Moderate', 'Coadministration of felodipine and erythromycin may increase felodipine serum concentrations. The presumed mechanism is inhibition of CYP450 3A4 metabolism by erythromycin. Troleandomycin and clarithromycin may also affect felodipine levels.', NULL, 'When macrolide antibiotics are coadministered with felodipine, patients should be monitored for adverse effects such as flushing, ankle edema, or palpitations.', NULL, 'When macrolide antibiotics are coadministered with felodipine, patients should be monitored for adverse effects such as flushing, ankle edema, or palpitations. Azithromycin and dirithromycin do not inhibit CYP450 and may be considered as alternatives.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
