        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1419124, 'Fluconazole', 'Erythromycin', 'Major', 'Coadministration with fluconazole may increase the plasma concentrations of erythromycin. The proposed mechanism is fluconazole inhibition of the CYP450 3A4-mediated metabolism of erythromycin. High dosages and plasma levels of erythromycin have been associated with QT interval prolongation and torsade de pointes arrhythmia. Fluconazole itself can also prolong the QT interval, thus an additive pharmacodynamic effect with erythromycin may occur as well.', NULL, 'Coadministration of erythromycin with fluconazole is considered contraindicated.', NULL, 'Coadministration of erythromycin with fluconazole is considered contraindicated.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
