        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1419135, 'Fosamprenavir', 'Erythromycin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of erythromycin, which is primarily metabolized by the isoenzyme. The use of erythromycin has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes. In addition, erythromycin may increase the plasma concentrations of drugs that are substrates of CYP450 3A4.', NULL, 'Concomitant use of erythromycin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of erythromycin with potent CYP450 3A4 inhibitors should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
