    ) VALUES (1419203, 'Erythromycin', 'Lincomycin', 'Moderate', 'Lincomycin derivatives and erythromycin have antagonistic effects in vitro. The mechanism is competitive binding of the 50S ribosomal subunit.', NULL, 'Clindamycin or lincomycin should not be used concurrently with erythromycin.', NULL, 'Clindamycin or lincomycin should not be used concurrently with erythromycin.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
