    ) VALUES (1419306, 'Erythromycin', 'Phenytoin', 'Moderate', 'Coadministration with phenytoin may reduce the plasma concentrations and antimicrobial efficacy of erythromycin and other macrolide antibiotics. The mechanism is induction of CYP450 3A metabolism by phenytoin. Additionally, plasma concentrations of phenytoin may be increased.', NULL, 'If concomitant use is medically necessary, caution and monitoring for altered efficacy and safety of both medications is recommended.', NULL, 'If concomitant use is medically necessary, caution and monitoring for altered efficacy and safety of both medications is recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
