    ) VALUES (1419337, 'Erythromycin', 'Quinine', 'Major', 'Coadministration with ketolide and macrolide antibiotics that inhibit CYP450 3A4 may significantly increase the plasma concentrations of quinine, which is primarily metabolized by the isoenzyme.', NULL, 'The use of quinine in combination with ketolide and macrolide antibiotics that inhibit CYP450 3A4 should generally be avoided.', NULL, 'The use of quinine in combination with ketolide and macrolide antibiotics that inhibit CYP450 3A4 should generally be avoided. Azithromycin may be a safer alternative during therapy with quinine.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
