    ) VALUES (1419391, 'Erythromycin', 'Suvorexant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of suvorexant, which is primarily metabolized by the isoenzyme.', NULL, 'The recommended dose of suvorexant is 5 mg when used with moderate CYP450 3A4 inhibitors, and generally should not exceed 10 mg.', NULL, 'The recommended dose of suvorexant is 5 mg when used with moderate CYP450 3A4 inhibitors, and generally should not exceed 10 mg.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
