INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (159818, 'Sirolimus', 'Rifampicin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein may significantly decrease the plasma concentrations and pharmacologic effects of sirolimus. The mechanism probably involves reduced absorption as well as accelerated clearance of sirolimus due to induction of both intestinal P-glycoprotein drug efflux transporter and hepatic/intestinal CYP450 3A4 isoenzymes.', NULL, 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, the use of sirolimus with potent inducers of CYP450 3A4 and/or P-gp is not recommended.', NULL, 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, the use of sirolimus with potent inducers of CYP450 3A4 and/or P-gp is not recommended. Alternative therapeutic agents with less enzyme induction potential should be considered.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159991/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767365208);
