INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419516, 'Etravirine', 'Efavirenz', 'Major', 'Coadministration with efavirenz or nevirapine may significantly decrease the plasma concentrations of etravirine. The proposed mechanism is induction of etravirine metabolism via CYP450 3A4.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, the use of etravirine in combination with efavirenz or nevirapine should be avoided. The combination is also not recommended because concurrent use of two or more nonnucleoside reverse transcriptase inhibitors in an antiretroviral regimen has not demonstrated beneficial effects.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, the use of etravirine in combination with efavirenz or nevirapine should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
