INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419561, 'Indacaterol', 'Efavirenz', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 1767317476, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419562, 'Indinavir', 'Efavirenz', 'Moderate', 'Coadministration with efavirenz may decrease the plasma concentrations of indinavir, even when ritonavir is given simultaneously to boost concentrations of indinavir. The mechanism is efavirenz induction of CYP450 3A4, which may override some of the inhibiting effects of ritonavir on the isoenzyme and accelerate the clearance of both indinavir and ritonavir.', 'DDInter', 1767317476, 'The manufacturer recommends that the dosage of indinavir be increased from 800 mg to 1000 mg every 8 hours when coadministered with efavirenz. When ritonavir is also given as a pharmacokinetic booster, limited data suggest it may be possible to overcome the effects of efavirenz by increasing the ritonavir dosage (e.g., 200 mg twice a day).', 'Metabolism', 'The manufacturer recommends that the dosage of indinavir be increased from 800 mg to 1000 mg every 8 hours when coadministered with efavirenz.', NULL, NULL, 'Moderate', 'pharmacodynamic');
