INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419577, 'Isavuconazonium', 'Efavirenz', 'Moderate', 'Coadministration of efavirenz and isavuconazonium sulfate (prodrug of isavuconazole) may result in a reduction in the plasma concentrations and therapeutic effects of both drugs. The proposed mechanisms involve efavirenz induction of CYP450 3A4/5-mediated metabolism of isavuconazole and isavuconazole induction of CYP450 2B6-mediated metabolism of efavirenz.', 'DDInter', 1767317476, 'Caution is advised during concomitant use of isavuconazonium sulfate with efavirenz. The potential for decreased efficacy of both drugs should be considered.', 'Metabolism', 'Caution is advised during concomitant use of isavuconazonium sulfate with efavirenz.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419578, 'Isoetharine', 'Efavirenz', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 1767317476, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
