INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419624, 'Efavirenz', 'Lurasidone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of lurasidone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of lurasidone should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of lurasidone should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
