INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419632, 'Efavirenz', 'Maraviroc', 'Major', 'Patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min) given maraviroc may have an increased risk of postural hypotension due to increased maraviroc exposure. Moreover, these patients often have cardiovascular comorbidities that could predispose them to adverse cardiovascular events triggered by postural hypotension. Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of maraviroc, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Maraviroc should be administered at a dosage of 600 mg twice daily during coadministration with potent CYP450 3A4 inducers. However, if a potent CYP450 3A4 inhibitor such as itraconazole, ketoconazole, delavirdine, clarithromycin, telithromycin, nefazodone, or any protease inhibitor (except tipranavir plus ritonavir) is also used in combination with the inducer, then maraviroc dosage should be reduced to 150 mg twice daily. Maraviroc is contraindicated for use with potent CYP450 3A4 inducers in patients with severe renal impairment or end-stage renal disease (CrCl <30 mL/min).', 'Metabolism', 'Maraviroc should be administered at a dosage of 600 mg twice daily during coadministration with potent CYP450 3A4 inducers.', NULL, NULL, 'Major', 'pharmacodynamic');
