INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419665, 'Efavirenz', 'Montelukast', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 2C9 may increase the plasma concentrations of montelukast, which is metabolized by these isoenzymes in addition to CYP450 3A4. When montelukast was administered with the potent CYP450 2C8/2C9 inhibitor gemfibrozil, montelukast systemic exposure (AUC) increased by 4.4-fold.', 'DDInter', 1767317476, 'According to the product labeling, no dosage adjustment of montelukast is required when used in combination with gemfibrozil. However, it may be advisable to monitor patients for potentially increased adverse effects during coadministration with gemfibrozil or other CYP450 2C8/2C9 inhibitors.', 'Metabolism', 'According to the product labeling, no dosage adjustment of montelukast is required when used in combination with gemfibrozil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419666, 'Efavirenz', 'Moxifloxacin', 'Major', 'Certain quinolones, including gatifloxacin and moxifloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 1767317476, 'Coadministration of gatifloxacin or moxifloxacin with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of the quinolone, recommended dosages and intravenous infusion rates should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of gatifloxacin or moxifloxacin with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
