INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419669, 'Efavirenz', 'Naloxegol', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of naloxegol, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'The potential for diminished therapeutic effects of naloxegol should be considered when prescribed with inducers of CYP450 3A4. Pharmacologic effects of naloxegol should be monitored more closely whenever an inducer is added to or withdrawn from therapy, and alternative treatment considered if an interaction is suspected.', 'Metabolism', 'The potential for diminished therapeutic effects of naloxegol should be considered when prescribed with inducers of CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
