INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (160310, 'St. John''s Wort', 'Remdesivir', 'Moderate', 'Theoretical concerns exist that coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of remdesivir, which has been shown in vitro to be a substrate of the metabolic isoenzyme and efflux transporter.', NULL, 'Until more information is available, some experts recommend avoiding the concomitant use of remdesivir with potent CYP450 3A4 or P-gp inducers.', NULL, 'Until more information is available, some experts recommend avoiding the concomitant use of remdesivir with potent CYP450 3A4 or P-gp inducers.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/160483/', '[1] US Food and Drug Administration "Fact sheet for health care providers emergency use authorization (EUA) of remdesivir (GS-5734TM) Available from: URL: https://www.fda.gov/media/137566/download." ([2020, May 1]]):[2] Gilead Sciences, Inc "About Remdesivir. Available from: URL: https://www.gilead.com/purpose/advancing-global-health/covid-19/about-remdesivir." ([2020]):[3] European Medicines Agency "Summary on compassionate use. Remdesivir Gilead. Available from: URL: https://www.ema.europa.eu/en/documents/other/summary-compassionate-use-remdesivir-gilead_en.pdf." ([2020 Apr 03]):[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363025);
