INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419734, 'Efavirenz', 'Pimozide', 'Major', 'Coadministration with efavirenz may increase the plasma concentrations of pimozide. The proposed mechanism is competitive inhibition of pimozide metabolism via CYP450 3A4. Elevated plasma levels of pimozide have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia, ventricular fibrillation, and torsade de pointes; cardiac arrest; and sudden death.', 'DDInter', 1767317476, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, the concomitant use of efavirenz is considered contraindicated.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of pimozide, the concomitant use of efavirenz is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
