INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419777, 'Efavirenz', 'Remdesivir', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', 'DDInter', 1767317476, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419778, 'Efavirenz', 'Repaglinide', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of repaglinide, which is metabolized by the isoenzyme in the intestine and liver.', 'DDInter', 1767317476, 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, patients treated concomitantly with rifampin may require a higher dosage of repaglinide. Blood glucose should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy in patients stabilized on their existing antidiabetic regimen, and the repaglinide dosage adjusted as necessary. Patients should be advised to contact their physician if they experience loss of glycemic control.', 'Metabolism', 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, patients treated concomitantly with rifampin may require a higher dosage of repaglinide.', NULL, NULL, 'Moderate', 'pharmacodynamic');
