INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419810, 'Efavirenz', 'Sildenafil', 'Moderate', 'Theoretically, coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of sildenafil, which is primarily metabolized by the isoenzyme. The possibility of a diminished therapeutic response to sildenafil should be considered.', 'DDInter', 1767317476, 'Pharmacologic response to sildenafil should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the sildenafil dosage adjusted if necessary.', 'Metabolism', 'Pharmacologic response to sildenafil should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the sildenafil dosage adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
