INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419906, 'Efavirenz', 'Voriconazole', 'Major', 'Coadministration of efavirenz and voriconazole may significantly reduce the plasma concentrations of voriconazole and increase the plasma concentrations of efavirenz. The mechanism involves efavirenz induction of voriconazole metabolism via CYP450 2C19, 2C9 and 3A4, and voriconazole inhibition of efavirenz metabolism via CYP450 3A4.', 'DDInter', 1767317476, 'Concomitant use of standard dosages of voriconazole with efavirenz dosages of 400 mg every 24 hours or higher is considered contraindicated. If concurrent administration is necessary, the manufacturer recommends that maintenance dosage of voriconazole be increased from 200 mg to 400 mg orally every 12 hours and that of efavirenz be reduced from 600 mg to 300 mg orally once daily. When treatment with voriconazole is stopped, the initial dosage of efavirenz should be restored (and vice versa). No dosage recommendations are available for the concomitant use of intravenous voriconazole and oral efavirenz.', 'Metabolism', 'Concomitant use of standard dosages of voriconazole with efavirenz dosages of 400 mg every 24 hours or higher is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419907, 'Efavirenz', 'Vortioxetine', 'Moderate', 'Coadministration with potent inducers of CYP450 isoenzymes may significantly decrease the plasma concentrations of vortioxetine, which is primarily metabolized by CYP450 2D6.', 'DDInter', 1767317476, 'An increase in the dosage of vortioxetine should be considered when used in combination with potent CYP450 inducers (e.g., carbamazepine, phenobarbital, phenytoin, rifamycins) for greater than 14 days, up to a maximum of three times the original dosage depending on clinical response. Following discontinuation of the potent CYP450 inducer, vortioxetine dosage should be returned to the original level within 14 days. Other known CYP450 inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, enzalutamide, efavirenz, etravirine, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they interact with vortioxetine is unknown.', 'Metabolism', 'An increase in the dosage of vortioxetine should be considered when used in combination with potent CYP450 inducers (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
