INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1419960, 'Tolvaptan', 'Potassium bicarbonate', 'Moderate', 'Coadministration with tolvaptan can potentiate the risk of hyperkalemia in patients treated with other drugs that are known to increase serum potassium. Tolvaptan alone can increase serum potassium by causing an acute reduction of the extracellular fluid volume.', 'DDInter', 1767317476, 'Serum potassium levels should be checked regularly during concomitant therapy with tolvaptan and other agents that can increase serum potassium such as ACE inhibitors, angiotensin II receptor antagonists, potassium-sparing diuretics, aldosterone antagonists, and miscellaneous agents like cyclosporin, heparin, tacrolimus, and trimethoprim. Particular caution is warranted in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', 'Serum potassium levels should be checked regularly during concomitant therapy with tolvaptan and other agents that can increase serum potassium such as ACE inhibitors, angiotensin II receptor antagonists, potassium-sparing diuretics, aldosterone antagonists, and miscellaneous agents like cyclosporin, heparin, tacrolimus, and trimethoprim.', NULL, NULL, 'Moderate', 'pharmacodynamic');
