    ) VALUES (1419994, 'Eszopiclone', 'Elagolix', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of zopiclone, which is a substrate of the isoenzyme.', NULL, 'Pharmacologic response to zopiclone and eszopiclone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hypnotic dosage adjusted if necessary.', NULL, 'Pharmacologic response to zopiclone and eszopiclone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hypnotic dosage adjusted if necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
