    ) VALUES (1420094, 'Elagolix', 'Pimavanserin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of pimavanserin, which is primarily metabolized by the isoenzyme.', NULL, 'The possibility of diminished therapeutic response to pimavanserin should be considered during coadministration with CYP450 3A4 inducers.', NULL, 'The possibility of diminished therapeutic response to pimavanserin should be considered during coadministration with CYP450 3A4 inducers. The manufacturer recommends coadministration with strong or moderate CYP450 3A4 inducers be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
