INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (160821, 'Ginseng', 'Reteplase', 'Moderate', 'Ginseng may potentiate the effects of anticoagulants, platelet inhibitors and thrombolytic agents, possibly increasing the risk of bleeding. In vitro studies have shown that one or more of the active constituents of ginseng, known as ginsenosides, can decrease platelet aggregation, although it is not known whether these constituents are absorbed or are bioactive in humans. Bleeding complications secondary to antiplatelet activity have not been reported.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In general, consumption of ginseng supplements and large amounts of ginseng should preferably be avoided during use of coagulation-modifying agents. In patients who have used this herb extensively prior to receiving anticoagulation, antiplatelet or thrombolytic therapy, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/160994/', '[1] Heck AM, DeWitt BA, Lukes AL "Potential interactions between alternative therapies and warfarin." Am J Health Syst Pharm 57 (2000): 1221-7; quiz 1228-30[2] Ang-Lee MK, Moss J, Yuan CS "Herbal medicines and perioperative care." JAMA 286 (2001): 208-16[3] Hodges PJ, Kam PC "The peri-operative implications of herbal medicines." Anaesthesia 57 (2002): 889-99[4] Izzo AA, Ernst E "Interactions between herbal medicines and pr