INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (160963, 'Boceprevir', 'Ribociclib', 'Major', 'Coadministration with potent CYP450 3A4 inhibitors may increase the plasma concentrations and the risk of adverse effects of ribociclib, which is a substrate of the isoenzyme. The risk of adverse effects such as infections, neutropenia, leucopenia, anemia, thrombocytopenia, nausea, vomiting, diarrhea, stomatitis, anorexia, alopecia, fatigue, headache, and abnormal liver function may be increased.', NULL, 'Concomitant use of ribociclib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of ribociclib with potent CYP450 3A4 inhibitors should generally be avoided. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist, the dose of ribociclib should be reduced to 400 mg once daily. Patients receiving concomitant treatment with a CYP450 3A4 inhibitor should be monitored for the development of ribociclib-related adverse effects. Following discontinuation of the potent CYP450 3A4 inhibitor, the ribociclib dosage should be returned (after at least 5 half-lives of the inhibitor) to that used prior to initiation of the inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161136/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (160964, 'Butabarbital', 'Ribociclib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ribociclib, which is a substrate of the isoenzyme.', NULL, 'The potential for diminished therapeutic effects of ribociclib should be considered when prescribed with inducers of CYP450 3A4, particularly moderate inducers including efavirenz and nevirapine.', NULL, 'The potential for diminished therapeutic effects of ribociclib should be considered when prescribed with inducers of CYP450 3A4, particularly moderate inducers including efavirenz and nevirapine. Pharmacologic effects of ribociclib should be monitored more closely whenever an inducer is added to or withdrawn from therapy. Alternative treatment may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161137/', '[1] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.[5] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (160965, 'Candida albicans', 'Ribociclib', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161138/', '[1] "Product Information. Spherulin (coccidioidin skin test)." ALK Laboratories Inc, Wallingford, CT.[2] "Product Information. Tuberculin PPD (Mantoux) (tuberculin purified protein derivative)." Connaught Laboratories Ltd, Toronto, ON.[3] "Product Information. Candin (candida albicans extract)." Nielsen Biosciences Inc, San Diego, CA.[4] "Product Information. MSTA Mumps Skin Test Antigen (mumps skin test antigen)." Aventis Pharmaceuticals, Swiftwater, PA.[5] "Product Information. Multitest CMI (skin test antigens, multiple)." Aventis Pharmaceuticals, Swiftwater, PA.[6] "Product Information. Histolyn-Cyl (histoplasmin)." ALK Laboratories Inc, Wallingford, CT.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (160966, 'Frangula purshiana bark', 'Ribociclib', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161139/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schaefer DC, Cheskin LJ "Constipation in the elderly." Am Fam Physician 58 (1998): 907-14[4] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Chin RL "Laxative-induced hyp