INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420303, 'Grazoprevir', 'St. John''s Wort', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of elbasvir and grazoprevir, both of which are substrates of the isoenzyme. Efavirenz is generally considered a moderate inducer of CYP450 3A4. More potent inducers such as carbamazepine or phenytoin may interact to an even greater extent.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of elbasvir-grazoprevir with efavirenz or potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of elbasvir-grazoprevir with efavirenz or potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
