INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420306, 'Grazoprevir', 'Sunitinib', 'Moderate', 'Coadministration of sunitinib with elbasvir-grazoprevir may increase the plasma concentrations and the risk of adverse effects of sunitinib. The proposed mechanism by which elbasvir-grazoprevir increases sunitinib exposure has not been clearly delineated, but may involve inhibition of intestinal breast cancer resistance protein (BCRP).', 'DDInter', 1767317476, 'Although the interaction has not been studied, caution is advised if sunitinib and elbasvir-grazoprevir are used in combination. Dose adjustment of sunitinib as well as clinical and laboratory monitoring of sunitinib should be considered whenever elbasvir-grazoprevir is added to or withdrawn from therapy. During concomitant treatment, patients should be monitored for signs and symptoms of sunitinib toxicity such as diarrhea, hypertension, and/or neutropenia.', 'Others', 'Although the interaction has not been studied, caution is advised if sunitinib and elbasvir-grazoprevir are used in combination.', NULL, NULL, 'Moderate', 'pharmacodynamic');
