INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420308, 'Grazoprevir', 'Tacrolimus', 'Moderate', 'Coadministration with elbasvir-grazoprevir may increase the blood concentrations of tacrolimus. The proposed mechanism is inhibition of the CYP450 3A4-mediated metabolism of tacrolimus by grazoprevir, a weak inhibitor of the isoenzyme.', 'DDInter', 1767317476, 'Given the narrow therapeutic index and the large interindividual variability in the pharmacokinetics of tacrolimus, caution is advised if the drug is used in combination with elbasvir-grazoprevir. Renal function and tacrolimus blood levels should be monitored more closely whenever elbasvir-grazoprevir is added to or discontinued from therapy, and the tacrolimus dosage adjusted as necessary. Patients should be advised to contact their doctor if they experience potential signs and symptoms of tacrolimus toxicity such as fever, infection, diarrhea, tremor, headache, fatigue, lethargy, and changes in motor function, mental status, or sensory function.', 'Metabolism', 'Given the narrow therapeutic index and the large interindividual variability in the pharmacokinetics of tacrolimus, caution is advised if the drug is used in combination with elbasvir-grazoprevir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420309, 'Elbasvir', 'Talazoparib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of talazoparib, which has been shown in vitro to be a substrate of both efflux membrane transporters.', 'DDInter', 1767317476, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil. However, patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Distribution', 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
