INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420470, 'Eletriptan', 'Oxitriptan', 'Major', 'Concomitant use of agents with serotonergic activity such as serotonin reuptake inhibitors, monoamine oxidase inhibitors, tricyclic antidepressants, 5-HT1 receptor agonists, ergot alkaloids, cyclobenzaprine, lithium, St. John''s wort, phenylpiperidine opioids, dextromethorphan, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucination, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'DDInter', 1767317476, 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is advised when increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life.', 'Synergism', 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420471, 'Amprenavir', 'Eletriptan', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eletriptan, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in increased risk of vasospastic reactions associated with the use of 5-HT1 receptor agonists, such as coronary artery vasospasm, peripheral vascular ischemia, and colonic ischemia.', 'DDInter', 1767317476, 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors. Alternatively, other 5-HT1 receptor agonists that are not metabolized by CYP450 3A4 may be considered, such as frovatriptan, naratriptan, rizatriptan, sumatriptan, and zolmitriptan.', 'Metabolism', 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420472, 'Eletriptan', 'Berotralstat', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eletriptan, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, nefazodone, delavirdine, most protease inhibitors, and ketolide and certain macrolide antibiotics. Patients should have vital signs monitored regularly and advised to notify their physician if they experience signs and symptoms of vasospasm such as numbness, tingling, or cyanosis in the extremities; muscle pains; weakness; or chest pain or tightness. Alternatively, other 5-HT1 receptor agonists that are not metabolized by CYP450 3A4 may be considered, such as frovatriptan, naratriptan, rizatriptan, sumatriptan, and zolmitriptan.', 'Metabolism', 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, nefazodone, delavirdine, most protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
