INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161517, 'Sunitinib', 'Rifapentine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of sunitinib and its pharmacologically active metabolite, both of which are substrates of the isoenzyme.', NULL, 'Concomitant use of sunitinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of sunitinib with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with minimal or no enzyme induction potential should be considered whenever possible. (Note: This recommendation is based on pharmacokinetic data from healthy volunteers as described above. The safety and efficacy of sunitinib with concomitant CYP450 3A4 inducers have not been established. )', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161690/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[4] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[5] "Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365208);
