INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161572, 'Entrectinib', 'Rifaximin', 'Moderate', 'Coadministration with entrectinib may increase the plasma concentrations of drugs that are substrates of the OATP 1B1 (organic anion transporting polypeptide 1B1) transporter. The proposed mechanism, based on in vitro data, is decreased clearance due to entrectinib-mediated inhibition of OATP1B1 transport protein.', NULL, 'Caution is advised when entrectinib is prescribed with drugs that are OATP1B1 substrates, particularly those with a narrow therapeutic range such as atorvastatin, pravastatin, rosuvastatin, repaglinide, and bosentan.', NULL, 'Caution is advised when entrectinib is prescribed with drugs that are OATP1B1 substrates, particularly those with a narrow therapeutic range such as atorvastatin, pravastatin, rosuvastatin, repaglinide, and bosentan. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever entrectinib is added to or withdrawn from therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161745/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[6] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161573, 'Enzalutamide', 'Rifaximin', 'Moderate', 'Coadministration with enzalutamide may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-glycoprotein (P-gp) efflux transporter. The proposed mechanism involves accelerated clearance via these routes due to enzalutamide-mediated induction. The resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', NULL, 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when enzalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enzalutamide is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161746/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Xtandi (enzalutamide)." Astellas Pharma US, Inc, Deerfield, IL.[3] Benoist G, van Oort I, et al "Drug-drug interaction potential in men treated with enzalutamide: Mind the gap." Br J Clin Pharmacol 0 (2017): epub[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161574, 'Erdafitinib', 'Rifaximin', 'Moderate', 'Coadministration with erdafitinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter, resulting in increased toxicity of these drugs. In vitro, erdafitinib has been shown to be an inhibitor of P-gp.', NULL, 'Concomitant use of erdafitinib and P-gp substrates should generally be avoided.', NULL, 'Concomitant use of erdafitinib and P-gp substrates should generally be avoided. If concomitant use is required, administer erdafitinib at least 6 hours before or after administration of P-gp substrates with narrow therapeutic ranges.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161747/', '[1] "Product Information. Balversa (erdafitinib)." Janssen Products, LP, Horsham, PA.', 1767365208);
