INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161576, 'Fostamatinib', 'Rifaximin', 'Moderate', 'Coadministration with fostamatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of CYP450 3A4-mediated metabolism and P-gp-mediated efflux by fostamatinib.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostamatinib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161749/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals, South San Francisco, CA.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161577, 'Fostemsavir', 'Rifaximin', 'Moderate', 'Coadministration with fostemsavir may increase the plasma concentrations of drugs that are substrates of the transporters, organic anion transporting polypeptides (OATP) 1B1/1B3 and breast cancer resistance protein (BCRP). Temsavir, the active moiety of fostemsavir, is an inhibitor of OATP 1B1 and 1B3. Additionally, temsavir and two metabolites are inhibitors of BCRP.', NULL, 'Caution is advised when fostemsavir is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when fostemsavir is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostemsavir is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161750/', '[1] "Product Information. Rukobia (fostemsavir)." ViiV Healthcare, Research Triangle Park, NC.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161578, 'Gemfibrozil', 'Rifaximin', 'Moderate', 'Coadministration with gemfibrozil may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 transporter. The mechanism is decreased clearance due to inhibition of OATP 1B1-mediated hepatic uptake by gemfibrozil and its metabolite, gemfibrozil 1-O-beta-glucuronide. The clinical relevance of this interaction is unknown.', NULL, 'A dosage reduction of OATP 1B1 substrates may be required when used with gemfibrozil.', NULL, 'A dosage reduction of OATP 1B1 substrates may be required when used with gemfibrozil. Clinical and laboratory monitoring may be appropriate for some drugs whenever gemfibrozil is added to or withdrawn from therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161751/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Lasix (furosemide)." sanofi-aventis , Bridgewater, NJ.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161579, 'Gilteritinib', 'Rifaximin', 'Moderate', 'Coadministration with gilteritinib may increase the plasma concentrations and the risk of adverse effects of orally administered drugs that are substrates of the intestinal P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), and/or organic cation transporter 1 (OCT1) transporters, such as dabigatran, digoxin, rosuvastatin, methotrexate, and metformin.', NULL, 'Until more information is available, caution is advised if gilteritinib is used concomitantly with drugs that are substrates of the intestinal P-gp, BCRP, and/or OCT1 transport proteins, particularly those with a narrow therapeutic range.', NULL, 'Until more information is available, caution is advised if gilteritinib is used concomitantly with drugs that are substrates of the intestinal P-gp, BCRP, and/or OCT1 transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever gilteritinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161752/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Xospata (gilteritinib)." Astellas Pharma US, Inc, Deerfield, IL.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365208);
